import Foundation
import Alamofire
class APIManager {
//MARK: singlton
    static let shared = APIManager()
    
     func getArtistData(baseURL: String, completion: @ escaping (_ error: Error?, _ artistArr: [ArtistData]? ) -> Void) {
        //let pram: [String: Any] = ["term": "adele"]
         AF.request(baseURL, method: HTTPMethod.get, parameters: nil, encoding: URLEncoding.default, headers: nil).response { response in
             print(response)
             
            guard response.error == nil else{
                //print(response.error!)
                completion(response.error, nil)
                return
            }
            
            guard let data = response.data else {
                print("server dosn't return data!")
                return
            }
             
            do {
                let artistData = try JSONDecoder().decode(Response.self, from: data)
                let artistArr = artistData.results
                completion(nil, artistArr)
                
                } catch let error {
                completion((error.localizedDescription as? Error), nil)
                  print(error.localizedDescription)
            }
        }
    }
}

struct Response: Codable {
    let resultCount: Int?
    let results: [ArtistData]?
}

struct ArtistData: Codable {
    let artistName: String?
    let artworkUrl100: String?
    let longDescription: String?
    let trackName: String?
    let previewUrl: String?
    let kind: String?
}





